<script>



    function checkAll(id, cid) {
        //alert(cid)
        $('.' + cid).not(id).prop('checked', id.checked);

        var final_id = cid.replace('checkAll', '');
        //value = value.replace(".", ":");


        if ($('#' + cid).is(":checked"))
        {
            // alert(final_id)
            $("#show" + final_id).show();
        } else {
            // alert(final_id)
            $("#show" + final_id).hide();

        }
    }

    function showMenu(id)
    {

        if ($('#' + id).is(":checked"))
        {
            $("#show" + id).show();
            $("#showview" + id).prop('checked', true);
         } else {
            $("#show" + id).hide();

        }

    }


    $(document).on('click', '#savemenuall', function () {
        var userid = $(this).parent().attr('id');

        if ($('input:checkbox:checked').length == 0)
        {
            alert("Please select at least one checkbox")
        } else
        {
            var formData = new FormData($(this).parents('form')[0]);

            var action = $(this).attr('id');
            $.ajax({
                url: '/permissions/' + action,
                type: 'POST',
                success: function (data, textStatus, xhr) {
                    //alert(data)
                    $("#message_show" + userid).show();
                    $("#message" + userid).html(data);

                },

                data: formData,
                cache: false,
                contentType: false,
                processData: false


            });
        }
    });
